import React from 'react';
import '../index.css';
import Logo from '../assets/logo.png';
import { Jumbotron, Button } from 'reactstrap';
import { UncontrolledCollapse, CardBody, Card } from 'reactstrap';
import axios from 'axios';

//Jumbotron Component

// axios orp

const ReactJumbotron = () => {

    return (
        <Jumbotron className="background-colour">
            <h1 className="display-3">Welcome To The Ethereum Lottery</h1>
            <img src={Logo} alt="My logo" />
            <br />
            <h2 className="lead">This Lottery is run and managed by [Owner Address] </h2>
            <br />
            <h2 className="lead">Currently, we have [Number] entries, completing to win [Amount] Ether!</h2>
            <br />
            <hr className="my-2" />
            <br />
            <p className="lead">
                &emsp;
                        <Button tag="p" color="primary" id="toggler" style={{ marginBottom: '1rem', width: '280px', height: '40px', fontSize: '0.9rem' }}>
                    Account Information
                        </Button>
                <UncontrolledCollapse toggler="#toggler">
                    <Card className="background-colour">
                        <CardBody>
                            Current Account: [Account Address]
                                </CardBody>
                        <CardBody>
                            Current Account Balance: [Account Balance] Ether
                                </CardBody>
                    </Card>
                </UncontrolledCollapse>
            </p>
        </Jumbotron>
    )
}

export default ReactJumbotron;
